let openAlphabetButton=document.querySelectorAll(".planner__open-countries-list"),alphabet=document.querySelector(".alphabet");openAlphabetButton.forEach(e=>e.addEventListener("click",(function(e){if(e.preventDefault(),alphabet.classList.add("alphabet--open"),alphabet.classList.contains("alphabet--open")){alphabet.querySelector(".button--close-alphabet").addEventListener("click",(function(e){e.preventDefault(),alphabet.classList.remove("alphabet--open")}))}})));