let showBusinessRatesButton=document.querySelector(".button--business-rates"),businessRatesPopup=document.querySelector(".business-rates");showBusinessRatesButton.addEventListener("click",(function(s){if(s.preventDefault(),businessRatesPopup.classList.add("business-rates--open"),businessRatesPopup.classList.contains("business-rates--open")){businessRatesPopup.querySelector(".business-rates__button").addEventListener("click",(function(s){s.preventDefault(),businessRatesPopup.classList.remove("business-rates--open")}))}}));